/* STYLED CONTAINERS */
.navbar {
    clear:both; display:block;
}
.navbar_link {
   font-size:70%; line-height:100%;
}
.navbar_button {
   font-size:80%; line-height:100%;
   display:block;
   text-align:center;
   border:1px solid #666;
   padding:4px;
   border-radius:0.5em;
   background:#ddd;
   box-shadow: 5px 5px 10px rgba(0,0,0,0.5);
}
.navbar_button:hover {
   background:#ccc;
   fill:blue;
}
.navbar_titlebox {
   display:block;
   width:85%;
   padding:0.5em 1em;
   margin:0em auto 0.25em auto;
   border:1px solid #999;
   border-radius:1em; 
   box-shadow: 5px 5px 10px rgba(0,0,0,0.5);
}
.navbar_icons {
   font-size:230%;
   float:left;
   margin:0 0.25em 0 0;
}
.navbar_caption {
   font-size:150%; line-height:100%;
   font-weight:bold;
   display:block;
}
.navbar_status {
   font-size:70%; line-height:100%;
   float:right;
}
.navbar_toolbar {
   font-size:70%; line-height:100%;
   display:block; text-align:center; margin:0 auto;
}
.navbar_tagpath {
   font-size:70%; line-height:100%;
}
.navbar_titlebox .tc-tag-list-item {
   margin:1px 0;
}
.navbar_titlebox .tc-tag-label {
   background-color:#eee;
   border:1px solid #999;
}
.navbar_titlebox .tc-tag-label:hover {
   background-color:#def;
   border:1px solid #66f;
}
.navbar_tiddler_info {
   display:block;
   font-size:90%;
   padding: 0.25em;
   border: 1px solid #999;
   border-radius: 0.5em;
   box-shadow: 5px 5px 10px rgba(0,0,0,0.3);
}

@media print {
.itw-toolbar,
.navbar_button,
.authortools
  { display:none; }
}

/* ROUNDED CORNERS ON TABBED TOC */
.tc-tabbed-table-of-contents .tc-table-of-contents {
  border-radius: 1em 0 0 1em;
}
.tc-tabbed-table-of-contents .tc-tabbed-table-of-contents-content {
  border-radius: 0 1em 1em 0;
}
.tc-tabbed-table-of-contents .tc-table-of-contents .toc-item-selected > a {
  border-radius: 0.5em 0 0 0.5em;
}

{{||InsideTW/NavBar/Top}}
<$transclude field="text" mode="block"/>
{{||InsideTW/NavBar/Bottom}}

.itw-TOC {
   position:fixed;
   z-index:1200;
   top: 2vh;
   left:3.5vw;
   margin:0;
   box-shadow: 5px 5px 10px rgba(0,0,0,0.5);
}
.tree_item {
      border:1px solid transparent;
      border-radius:0.4em;
      padding:0 0.25em;
      margin-right:2px;
}
.tree_item.selected {
      border-color:#66f;
      background:#ddf;
}
.invisible { visibility:hidden; }

/* PREVENT NESTED "ROLLUP" DISPLAY */
.viewall .viewall { display:none; }

/* border styling for each topic page */
.viewall .pageView {
   display:block;
   border:1px solid gray;
   margin:0 0 1em 0;
   padding:0 0 0 1em;
   border-radius:0.5em;
   box-shadow: 5px 5px 10px rgba(0,0,0,0.3);
}
